import React from "react";
import {
  Home,
  Receipt,
  BarChart3,
  Wallet,
  Bell,
  User,
  Settings,
  LogOut,
  Plus
} from "lucide-react";

const Sidebar = ({ 
  activeTab, 
  onTabChange, 
  onLogout, 
  user, 
  isMobileOpen, 
  onMobileClose 
}) => {
  const handleTabChange = (tabName) => {
    onTabChange(tabName);
    // Close mobile sidebar when a tab is selected
    if (onMobileClose) {
      onMobileClose();
    }
  };

  const NavItem = ({ icon: Icon, label, tabName, isActive = false }) => (
    <button 
      className={`nav-item ${isActive ? 'active' : ''}`}
      onClick={() => handleTabChange(tabName)}
    >
      <Icon size={18} /> {label}
    </button>
  );

  const NavLink = ({ icon: Icon, label, tabName, isActive = false }) => (
    <button 
      className={`nav-item ${isActive ? 'active' : ''}`}
      onClick={() => handleTabChange(tabName)}
    >
      <Icon size={18} /> {label}
    </button>
  );

  return (
    <aside className={`sidebar ${isMobileOpen ? 'mobile-open' : ''}`}>
      {/* Logo */}
      <div className="sidebar-logo">
        Expense<span>Track</span>
      </div>

      {/* Primary Action */}
      <button className="add-expense-btn" onClick={() => handleTabChange('dashboard')}>
        <Plus size={18} />
        New Expense
      </button>

      {/* Navigation */}
      <nav className="sidebar-nav">
        <NavItem icon={Home} label="Dashboard" tabName="dashboard" isActive={activeTab === 'dashboard'} />
        <NavItem icon={Receipt} label="Expenses" tabName="expenses" isActive={activeTab === 'expenses'} />
        <NavItem icon={BarChart3} label="Reports" tabName="analytics" isActive={activeTab === 'analytics'} />
        <NavItem icon={Wallet} label="Budgets" tabName="budgets" isActive={activeTab === 'budgets'} />
        <NavItem icon={Bell} label="Alerts" tabName="dashboard" isActive={false} />
      </nav>

      {/* Bottom Section */}
      <div className="sidebar-bottom">
        <NavLink icon={User} label="Profile" tabName="profile" isActive={activeTab === 'profile'} />
        <NavLink icon={Settings} label="Settings" tabName="dashboard" isActive={false} />
        <button className="logout-btn" onClick={onLogout}>
          <LogOut size={18} /> Logout
        </button>
      </div>
    </aside>
  );
};

export default Sidebar;
