import React, { useState } from 'react';
import '../../styles/Sidebar.css';

const Sidebar = ({ activeTab, onTabChange, onLogout, user, isMobileOpen = false, onMobileClose }) => {
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);

  React.useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth <= 768);
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);
  const menuItems = [
    { id: 'dashboard', label: 'Home', icon: 'ðŸ ' },
    { id: 'expenses', label: 'Expenses', icon: 'ðŸ’³' },
    { id: 'analytics', label: 'Reports', icon: 'ðŸ“Š' },
    { id: 'budgets', label: 'Budgets', icon: 'ðŸ“ˆ' },
    { id: 'profile', label: 'Settings', icon: 'âš™ï¸' }
  ];

  const handleMenuClick = (itemId) => {
    onTabChange(itemId);
    if (isMobile && onMobileClose) {
      onMobileClose();
    }
  };

  const handleLogout = () => {
    onLogout();
    if (isMobile && onMobileClose) {
      onMobileClose();
    }
  };

  return (
    <>
      {/* Mobile Overlay */}
      {isMobile && (
        <div 
          className={`sidebar-overlay ${isMobileOpen ? 'active' : ''}`}
          onClick={onMobileClose}
        />
      )}

      <div className={`dashboard-sidebar ${isMobile && isMobileOpen ? 'mobile-open' : ''}`}>
        <div className="sidebar-user">
          <p className="user-name">{user?.name || 'User'}</p>
        </div>

        <nav className="sidebar-nav">
          <ul className="nav-menu">
            {menuItems.map((item) => (
              <li key={item.id} className="nav-item">
                <button
                  className={`nav-link ${activeTab === item.id ? 'active' : ''}`}
                  onClick={() => handleMenuClick(item.id)}
                >
                  <span className="nav-icon">{item.icon}</span>
                  <span className="nav-label">{item.label}</span>
                </button>
              </li>
            ))}
          </ul>
        </nav>

        <div className="sidebar-footer">
          <button className="logout-btn" onClick={handleLogout}>
            <span className="logout-icon">ðŸ‘‹</span>
            <span className="logout-label">Sign Out</span>
          </button>
        </div>
      </div>
    </>
  );
};

export default Sidebar;
