import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Sidebar from './Sidebar';
import SummaryCards from './SummaryCards';
import ExpenseForm from './ExpenseForm';
import ExpenseList from './ExpenseList';
import Analytics from './Analytics';
import BudgetSection from './BudgetSection';
import Notifications from './Notifications';
import FloatingAddButton from './FloatingAddButton';
import { useAuth } from '../../App';
import './Dashboard.css';

const Dashboard = () => {
  const navigate = useNavigate();
  const { logout } = useAuth();
  const [showExpenseForm, setShowExpenseForm] = useState(false);
  const [activeTab, setActiveTab] = useState('dashboard');
  const [expenses, setExpenses] = useState([]);
  const [budgets, setBudgets] = useState([]);
  const [notifications, setNotifications] = useState([]);
  const [user, setUser] = useState(null);
  const [isMobileSidebarOpen, setIsMobileSidebarOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);

  // Handle window resize
  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth <= 768);
      if (window.innerWidth > 768) {
        setIsMobileSidebarOpen(false);
      }
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Load data from localStorage on component mount
  useEffect(() => {
    const savedUser = localStorage.getItem('user');
    const savedExpenses = localStorage.getItem('expenses');
    const savedBudgets = localStorage.getItem('budgets');
    const savedNotifications = localStorage.getItem('notifications');

    if (savedUser) setUser(JSON.parse(savedUser));
    if (savedExpenses) {
      try {
        const parsedExpenses = JSON.parse(savedExpenses);
        setExpenses(Array.isArray(parsedExpenses) ? parsedExpenses : []);
      } catch (error) {
        console.error('Error parsing expenses from localStorage:', error);
        setExpenses([]);
      }
    }
    if (savedBudgets) {
      try {
        const parsedBudgets = JSON.parse(savedBudgets);
        setBudgets(Array.isArray(parsedBudgets) ? parsedBudgets : []);
      } catch (error) {
        console.error('Error parsing budgets from localStorage:', error);
        setBudgets([]);
      }
    }
    if (savedNotifications) setNotifications(JSON.parse(savedNotifications));

    // Initialize with sample data if no data exists
    if (!savedExpenses) {
      const sampleExpenses = [
        {
          id: '1',
          amount: 1500,
          category: 'Salary',
          date: '2024-01-01',
          note: 'Monthly salary',
          type: 'income'
        },
        {
          id: '2',
          amount: 500,
          category: 'Food',
          date: '2024-01-02',
          note: 'Groceries',
          type: 'expense'
        },
        {
          id: '3',
          amount: 1200,
          category: 'Rent',
          date: '2024-01-01',
          note: 'Monthly rent',
          type: 'expense'
        }
      ];
      setExpenses(sampleExpenses);
      localStorage.setItem('expenses', JSON.stringify(sampleExpenses));
    }

    if (!savedBudgets) {
      const sampleBudgets = [
        { category: 'Food', limit: 1000, spent: 500, period: 'monthly' },
        { category: 'Travel', limit: 500, spent: 200, period: 'monthly' },
        { category: 'Entertainment', limit: 300, spent: 150, period: 'monthly' }
      ];
      setBudgets(sampleBudgets);
      localStorage.setItem('budgets', JSON.stringify(sampleBudgets));
    }
  }, []);

  const handleLogout = () => {
    // Clear authentication state
    logout();
    // Remove local user data
    localStorage.removeItem('user');
    // Navigate to landing page
    navigate('/');
  };

  const toggleMobileSidebar = () => {
    setIsMobileSidebarOpen(!isMobileSidebarOpen);
  };

  const closeMobileSidebar = () => {
    setIsMobileSidebarOpen(false);
  };

  const handleAddExpense = (expenseData) => {
    const newExpense = {
      ...expenseData,
      id: Date.now().toString(),
      date: new Date().toISOString().split('T')[0]
    };
    setExpenses(prev => [...prev, newExpense]);
    setShowExpenseForm(false);
  };

  const handleDeleteExpense = (expenseId) => {
    setExpenses(prev => prev.filter(exp => exp.id !== expenseId));
  };

  const handleEditExpense = (expenseId, updatedData) => {
    setExpenses(prev => prev.map(exp => 
      exp.id === expenseId ? { ...exp, ...updatedData } : exp
    ));
  };

  const renderMainContent = () => {
    switch (activeTab) {
      case 'dashboard':
        return (
          <div className="dashboard-main">
            <div className="dashboard-header">
              <h1>Home</h1>
              <p>Welcome back, {user?.name || 'User'}!</p>
            </div>

            <SummaryCards expenses={expenses} />

            <div className="dashboard-grid">
              <div className="dashboard-section">
                <h2>Recent Expenses</h2>
                <ExpenseList 
                  expenses={expenses} 
                  onDeleteExpense={handleDeleteExpense}
                  onEditExpense={handleEditExpense}
                  showActions={true}
                />
              </div>

              <div className="dashboard-section">
                <h2>Budget Overview</h2>
                <BudgetSection budgets={budgets} />
              </div>
            </div>

            <div className="dashboard-section">
              <h2>Analytics</h2>
              <Analytics expenses={expenses} budgets={budgets} />
            </div>
          </div>
        );

      case 'expenses':
        return (
          <div className="dashboard-main">
            <div className="dashboard-header">
              <h1>Expense Management</h1>
              <p>Track and manage all your expenses</p>
            </div>

            <div className="dashboard-section">
              <ExpenseList 
                expenses={expenses} 
                onDeleteExpense={handleDeleteExpense}
                onEditExpense={handleEditExpense}
                showActions={true}
              />
            </div>
          </div>
        );

      case 'analytics':
        return (
          <div className="dashboard-main">
            <div className="dashboard-header">
              <h1>Reports</h1>
              <p>Insights into your spending patterns</p>
            </div>

            <div className="dashboard-section">
              <Analytics expenses={expenses} budgets={budgets} />
            </div>
          </div>
        );

      case 'budgets':
        return (
          <div className="dashboard-main">
            <div className="dashboard-header">
              <h1>Budget Management</h1>
              <p>Set and monitor your spending limits</p>
            </div>

            <div className="dashboard-section">
              <BudgetSection budgets={budgets} />
            </div>
          </div>
        );

      case 'profile':
        return (
          <div className="dashboard-main">
            <div className="dashboard-header">
              <h1>Settings</h1>
              <p>Manage your account preferences</p>
            </div>

            <div className="dashboard-section">
              <div className="profile-content">
                <div className="profile-card">
                  <h3>User Information</h3>
                  <p><strong>Name:</strong> {user?.name || 'User'}</p>
                  <p><strong>Email:</strong> {user?.email || 'user@example.com'}</p>
                  <p><strong>Member Since:</strong> January 2024</p>
                </div>

                <div className="profile-card">
                  <h3>Preferences</h3>
                  <div className="preference-item">
                    <label>Currency</label>
                    <select defaultValue="USD">
                      <option value="USD">USD ($)</option>
                      <option value="EUR">EUR (€)</option>
                      <option value="GBP">GBP (£)</option>
                    </select>
                  </div>
                  <div className="preference-item">
                    <label>Date Format</label>
                    <select defaultValue="MM/DD/YYYY">
                      <option value="MM/DD/YYYY">MM/DD/YYYY</option>
                      <option value="DD/MM/YYYY">DD/MM/YYYY</option>
                      <option value="YYYY-MM-DD">YYYY-MM-DD</option>
                    </select>
                  </div>
                </div>
              </div>
            </div>
          </div>
        );

      default:
        return <div>Page not found</div>;
    }
  };

  return (
    <div className="dashboard-container">
      <Sidebar 
        activeTab={activeTab}
        onTabChange={setActiveTab}
        onLogout={handleLogout}
        user={user}
        isMobileOpen={isMobileSidebarOpen}
        onMobileClose={closeMobileSidebar}
      />

      <div className="dashboard-content">
        {/* Mobile Menu Button */}
        {isMobile && (
          <button 
            className="mobile-menu-btn"
            onClick={toggleMobileSidebar}
            aria-label="Toggle sidebar"
          >
            <span className="hamburger-line"></span>
            <span className="hamburger-line"></span>
            <span className="hamburger-line"></span>
          </button>
        )}
        {renderMainContent()}
      </div>

      {activeTab === 'dashboard' && (
        <FloatingAddButton onClick={() => setShowExpenseForm(true)} />
      )}

      {showExpenseForm && (
        <ExpenseForm
          onClose={() => setShowExpenseForm(false)}
          onSubmit={handleAddExpense}
        />
      )}

      <Notifications 
        notifications={notifications}
        onDismiss={(id) => setNotifications(prev => prev.filter(n => n.id !== id))}
      />
    </div>
  );
};

export default Dashboard;
