import {
  Home,
  Receipt,
  BarChart3,
  Wallet,
  Bell,
  User,
  Settings,
  LogOut,
  Plus
} from "lucide-react";
import '../../styles/Sidebar.css';

const Sidebar = ({ activeTab, onTabChange, onLogout, user, isMobileOpen, onMobileClose }) => {
  const handleNavClick = (tab) => {
    onTabChange(tab);
    // Close mobile sidebar if open
    if (isMobileOpen && onMobileClose) {
      onMobileClose();
    }
  };

  return (
    <aside className={`sidebar ${isMobileOpen ? 'mobile-open' : ''}`}>
      {/* Logo */}
      <div className="sidebar-logo">
        Expense<span>Track</span>
      </div>

      {/* Primary Action */}
      <button className="add-expense-btn">
        <Plus size={18} />
        New Expense
      </button>

      {/* Navigation */}
      <nav className="sidebar-nav">
        <button 
          onClick={() => handleNavClick('dashboard')} 
          className={`nav-item ${activeTab === 'dashboard' ? 'active' : ''}`}
        >
          <Home size={18} /> Dashboard
        </button>

        <button 
          onClick={() => handleNavClick('expenses')} 
          className={`nav-item ${activeTab === 'expenses' ? 'active' : ''}`}
        >
          <Receipt size={18} /> Expenses
        </button>

        <button 
          onClick={() => handleNavClick('analytics')} 
          className={`nav-item ${activeTab === 'analytics' ? 'active' : ''}`}
        >
          <BarChart3 size={18} /> Reports
        </button>

        <button 
          onClick={() => handleNavClick('budgets')} 
          className={`nav-item ${activeTab === 'budgets' ? 'active' : ''}`}
        >
          <Wallet size={18} /> Budgets
        </button>

        <button 
          onClick={() => handleNavClick('alerts')} 
          className={`nav-item ${activeTab === 'alerts' ? 'active' : ''}`}
        >
          <Bell size={18} /> Alerts
        </button>
      </nav>

      {/* Bottom Section */}
      <div className="sidebar-bottom">
        <button 
          onClick={() => handleNavClick('profile')} 
          className={`nav-item ${activeTab === 'profile' ? 'active' : ''}`}
        >
          <User size={18} /> Profile
        </button>

        <button 
          onClick={() => handleNavClick('settings')} 
          className={`nav-item ${activeTab === 'settings' ? 'active' : ''}`}
        >
          <Settings size={18} /> Settings
        </button>

        <button className="logout-btn" onClick={onLogout}>
          <LogOut size={18} /> Logout
        </button>
      </div>

      {/* Mobile Overlay */}
      {isMobileOpen && (
        <div className="sidebar-overlay" onClick={onMobileClose}></div>
      )}
    </aside>
  );
};

export default Sidebar;
