import React, { useState, useEffect } from 'react';
import { Routes, Route, useNavigate, useLocation } from 'react-router-dom';
import Sidebar from './Sidebar';
import SummaryCards from './SummaryCards';
import ExpenseForm from './ExpenseForm';
import ExpenseList from './ExpenseList';
import Analytics from './Analytics';
import BudgetSection from './BudgetSection';
import Notifications from './Notifications';
import FloatingAddButton from './FloatingAddButton';
import Profile from './Profile';
import { useAuth } from '../../App';
import '../../styles/Dashboard.css';

// Dashboard Home Page Component
const DashboardHome = ({ expenses, budgets, user, onDeleteExpense, onEditExpense }) => {
  return (
    <div className="dashboard-main">
      <div className="dashboard-header">
        <h1>Home</h1>
        <p>Welcome back, {user?.name || 'User'}!</p>
      </div>

      <SummaryCards expenses={expenses} />

      <div className="dashboard-grid">
        <div className="dashboard-section">
          <h2>Recent Expenses</h2>
          <ExpenseList 
            expenses={expenses} 
            onDeleteExpense={onDeleteExpense}
            onEditExpense={onEditExpense}
            showActions={true}
          />
        </div>

        <div className="dashboard-section">
          <h2>Budget Overview</h2>
          <BudgetSection budgets={budgets} />
        </div>
      </div>

      <div className="dashboard-section">
        <h2>Analytics</h2>
        <Analytics expenses={expenses} budgets={budgets} />
      </div>
    </div>
  );
};

// Other pages remain the same
const ExpensesPage = ({ expenses, onDeleteExpense, onEditExpense }) => { /* ... */ };
const ReportsPage = ({ expenses, budgets }) => { /* ... */ };
const BudgetsPage = ({ budgets }) => { /* ... */ };
const AlertsPage = () => { /* ... */ };
const SettingsPage = () => { /* ... */ };

const Dashboard = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { logout } = useAuth();
  const [showExpenseForm, setShowExpenseForm] = useState(false);
  const [expenses, setExpenses] = useState([]);
  const [budgets, setBudgets] = useState([]);
  const [notifications, setNotifications] = useState([]);
  const [user, setUser] = useState(null);
  const [isMobileSidebarOpen, setIsMobileSidebarOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);

  // Handle window resize
  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth <= 768);
      if (window.innerWidth > 768) setIsMobileSidebarOpen(false);
    };
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Load data from localStorage
  useEffect(() => {
    const savedUser = localStorage.getItem('user');
    const savedExpenses = localStorage.getItem('expenses');
    const savedBudgets = localStorage.getItem('budgets');
    const savedNotifications = localStorage.getItem('notifications');

    if (savedUser) setUser(JSON.parse(savedUser));
    if (savedExpenses) setExpenses(JSON.parse(savedExpenses) || []);
    if (savedBudgets) setBudgets(JSON.parse(savedBudgets) || []);
    if (savedNotifications) setNotifications(JSON.parse(savedNotifications) || []);
  }, []);

  const handleLogout = () => {
    logout();
    localStorage.removeItem('user');
    navigate('/');
  };

  const toggleMobileSidebar = () => setIsMobileSidebarOpen(!isMobileSidebarOpen);
  const closeMobileSidebar = () => setIsMobileSidebarOpen(false);

  const handleAddExpense = (expenseData) => {
    const newExpense = { ...expenseData, id: Date.now().toString(), date: new Date().toISOString().split('T')[0] };
    setExpenses(prev => [...prev, newExpense]);
    setShowExpenseForm(false);
  };

  const handleDeleteExpense = (expenseId) => setExpenses(prev => prev.filter(exp => exp.id !== expenseId));
  const handleEditExpense = (expenseId, updatedData) => {
    setExpenses(prev => prev.map(exp => exp.id === expenseId ? { ...exp, ...updatedData } : exp));
  };

  const isDashboardHome = location.pathname === '/dashboard';

  return (
    <div className="dashboard-container">
      <Sidebar 
        onLogout={handleLogout}
        user={user}
        isMobileOpen={isMobileSidebarOpen}
        onMobileClose={closeMobileSidebar}
      />

      <div className="dashboard-content">
        {isMobile && (
          <button className="mobile-menu-btn" onClick={toggleMobileSidebar} aria-label="Toggle sidebar">
            <span className="hamburger-line"></span>
            <span className="hamburger-line"></span>
            <span className="hamburger-line"></span>
          </button>
        )}

        <Routes>
          {/* Use relative paths */}
          <Route index element={
            <DashboardHome 
              expenses={expenses}
              budgets={budgets}
              user={user}
              onDeleteExpense={handleDeleteExpense}
              onEditExpense={handleEditExpense}
            />
          } />
          <Route path="expenses" element={
            <ExpensesPage 
              expenses={expenses}
              onDeleteExpense={handleDeleteExpense}
              onEditExpense={handleEditExpense}
            />
          } />
          <Route path="reports" element={<ReportsPage expenses={expenses} budgets={budgets} />} />
          <Route path="budgets" element={<BudgetsPage budgets={budgets} />} />
          <Route path="alerts" element={<AlertsPage />} />
          <Route path="profile" element={<Profile user={user} onUpdateUser={setUser} />} />
          <Route path="settings" element={<SettingsPage />} />
        </Routes>
      </div>

      {isDashboardHome && <FloatingAddButton onClick={() => setShowExpenseForm(true)} />}

      {showExpenseForm && <ExpenseForm onClose={() => setShowExpenseForm(false)} onSubmit={handleAddExpense} />}
      <Notifications 
        notifications={notifications}
        onDismiss={(id) => setNotifications(prev => prev.filter(n => n.id !== id))}
      />
    </div>
  );
};

export default Dashboard;
